import React, { useState } from 'react';
import '../estilos/App2.css';
import { FaArrowAltCircleRight, FaArrowAltCircleLeft } from 'react-icons/fa';
import "../estilos/Slider.css"
import imgcarrito from '../img/Logosyrecursos/Carrito.PNG'
import { arrayproductosGLOBAL } from '../componentes/globales'
var cantidades = 1
var total = 0.0 

//Agregar valor a arreglo de session
const agregarElemento = (tipoproducto,costo) => {

  cantidades = cantidades + 1
  total = cantidades * costo
  var arrayproductosPASO = 
    {
      producto:tipoproducto,		
        preciounitario:costo,
        cantidad:0,
        preciototal:0.0
    }
    console.log(tipoproducto);
    console.log(costo);
  //const [arrayproductos, setarrayproductos] = useState(arrayproductosGLOBAL);

 const resultante = [...arrayproductosGLOBAL,...arrayproductosPASO]
  localStorage.setItem('arrayproductos', JSON.stringify(resultante));
  
  if (localStorage.getItem("arrayproductos")){
    var objeto = JSON.parse(localStorage.getItem("arrayproductos"));
    console.log(objeto);
    

  }else {
    console.log("no hay nada");
  }
  
}



const ImageSlider = ({ slides }) => {
  const [current, setCurrent] = useState(0);
  const length = slides.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }


  return (
    <section className='slider'>
      <FaArrowAltCircleLeft className='left-arrow' onClick={prevSlide} />
      <FaArrowAltCircleRight className='right-arrow' onClick={nextSlide} />
      {slides.map((slide, index) => {
        return (
          <div
            className={index === current ? 'slide active' : 'slide'}
            key={index}
          >
            {index === current && (
               <img src={slide.image} alt='travel image' className='image' />  
              
            )}
            <button onClick={agregarElemento(slide.tipoproducto,slide.costo)}  className="imageCarrito" src={imgcarrito}></button>
					{/* <a href= {`${"/carritoback/"}${slide.id}`}>                             
    
              <img className="imageCarrito" src={imgcarrito} />
              </a>	 */}
         
          </div>      								    
        );
      })}
    </section>
  );
};

export default ImageSlider;
