import React from 'react'
import Imagen1 from '../img/Logosyrecursos/fda.png'
import Imagen from '../img/Imágenesdeproductos/MIGAS_PIGMENTOS/3.png'
import '../estilos/pagina.css'
class Pagina extends React.Component {
    render(){
        const {title,description,img,leftColor,rightColor} = this.props
        return (
        
                    <div className="card mx-auto Fitness-Card"
                      style={{
                        backgroundImage:`url(${Imagen1}) linear-gradient(to right, ${rightColor}, ${leftColor})`
                    }}   
                    >
                   
                            <div className="card-body"> 
                                <div className="row center">
                                    <div className="col-6">
                                        <img src={Imagen1} className="float-right"/>
                                    </div>               
                                </div>
                            </div>
                        </div>                
      
        )
       
    }

}

export default Pagina
