import React from 'react'
import { Table } from 'react-bootstrap';
import { variableslocales } from '../componentes/globales'
class Carrito extends React.Component {

    state = {}

    handleSubmit = e => {
        e.preventDefault()
        console.log(this.state)
    }

    handleChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
  
    render(){
        var contadorproductostrans = 0
        var contadorproductospolvos = 0
        var contadorproductosmigas = 0
        var totalcostoproductostrans = 0
        var totalcostoproductospolvos = 0
        var totalcostoproductosmigas = 0
        var costounitariotrans = 0
        var costounitariopolvos = 0
        var totalcostoproductosmigas = 0
        if (variableslocales.getItem("variableslocales")){
            var objeto = JSON.parse(variableslocales.getItem("variableslocales"));
            //console.log(objeto);
            for (var i in objeto) {
            console.log(objeto[i].getItem("producto"));
            //  if (objeto[i].getItem("producto") == 'translucidos'){
            //     contadorproductostrans = contadorproductostrans + 1
            //     costounitariotrans = objeto[i].getItem("preciounitario")
            //     totalcostoproductostrans = (contadorproductostrans * costounitariotrans)
            //  }
            //  if (objeto[i].getItem("producto") == 'polvo'){
            //     contadorproductospolvos = contadorproductospolvos + 1
            //  }
            //  if (objeto[i].getItem("producto") == 'migas'){
            //     contadorproductosmigas = contadorproductosmigas + 1
            //  }
          }
        
          }else {
           console.log("no hay nada");
          }
        //   const productosdinamicos = (producto,index) =>{
        //       return (
        //         <tr>
        //             <td>
        //             {producto.}
        //             </td>
        //         </tr>
        //       )                                
        //   }
        return (
            
            <div className="container">
            <form 
                onSubmit={this.handleSubmit}
            >
                <div className="form-group">
                <Table striped bordered hover variant="dark">
                        <thead>
                            <tr>
                            <th>#</th>
                            <th>Producto</th>
                            <th>Precio unitario</th>
                            <th>Cantidad</th>
                            <th>Precio Total</th>
                            </tr>
                        </thead>
                        <tbody>
                        
                          
                                    <tr>
                                    <td>1</td>
                                    <td>Mark</td>
                                    <td>Otto</td>
                                    <td>@mdo</td>
                                    <td>@mdo</td>
                                </tr>
                           
                           
                        </tbody>
                </Table>

                </div>
                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control" 
                        placeholder="title" 
                        name="title"
                        onChange={this.handleChange}
                        value={this.state.title}
                    />
                </div>
                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control" 
                        placeholder="description" 
                        name="description"
                        onChange={this.handleChange}
                        value={this.state.description}
                    />
                </div>
                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control" 
                        placeholder="img" 
                        name="img"
                        onChange={this.handleChange}
                        value={this.state.img}
                    />
                </div>
                <div className="form-row">
                    <div className="col">
                        <input 
                            type="text" 
                            className="form-control" 
                            placeholder="leftColor" 
                            name="leftColor"
                            onChange={this.handleChange}
                            value={this.state.leftColor}
                        />
                    </div>
                    <div className="col">
                        <input 
                            type="text" 
                            className="form-control"
                            placeholder="rightColor" 
                            name="rightColor"
                            onChange={this.handleChange}
                            value={this.state.rightColor}
                        />    
                    </div>
                </div>
                
                <button 
                    type="submit" 
                    className="btn btn-primary"
                >
                    Submit
                </button>
            </form>
        </div>
        )
    }
}

export default Carrito
